// Helper functions
func countAdmins(users []models.User) int {
	count := 0
	for _, user := range users {
		if user.Role == "admin" {
			count++
		}
	}
	return count
}

func countUsers(users []models.User) int {
	count := 0
	for _, user := range users {
		if user.Role == "user" {
			count++
		}
	}
	return count
}

